// hooks it is finction based componant
import React, {useState} from "react";

export default function Textform(props) {
    // using this function if we can clicked the convert uppercase button the value in the textbox convert to thr uppercase
    const handleupClick=()=>{
        let newText= text.toLocaleUpperCase();
        setText(newText)
    }
    // 
    const handleOnChange=(event)=>{
        // console.log("Onchange");
        setText(event.target.value)
    }
    
const [text,setText]= useState('Enter Text Here');
  return (
    <div>
    {/* //we can assign the props */}
      <h1>{props.heading}</h1>
      <div className="mb-3">
            {/* <label for="MyTExt" class="form-label">
            Enter text
            </label> */}
        <textarea
          className="form-control"
          value={text}
          onChange={handleOnChange}
          id="exampleFormControlTextarea1"
          rows="18"
          
        ></textarea>
      </div>
      <button type="button" className="btn btn-primary" onClick={handleupClick}>convert to uppercase</button>
    </div>
  );
}
